entity.entry:
{
  "character": {
    "name": "Eldrath",
    "description": "Eldrath is an ancient and formidable sorcerer who made a dark pact, selling his soul in exchange for forbidden knowledge and immense power.",
    "details": {
      "title": "Abilities and Obsessions",
      "special_abilities": "Eldrath's mastery of dark magic allows him to manipulate the magical energies of others and summon terrifying creatures from the darkest planes of existence. He is a master manipulator of arcane forces.",
      "background": "Driven by an insatiable desire to dominate all aspects of magic, Eldrath turned to a powerful Patron, making a deal to obtain powers far exceeding human limitations. This obsession has consumed him, driving his every action."
    },
    "connections_to_party": {
      "title": "Relationships and Conflicts",
      "urna_di_geb": {
        "description": "Eldrath's primary goal involves acquiring the Urn of Geb, an ancient artifact rumored to contain immense elemental power. He believes absorbing this power will further enhance his own magical abilities and bring him closer to his ultimate goal of magical domination.",
        "motivation": "Seeks to absorb its elemental powers to enhance his own."
      },
      "vordekai_rivalry": {
        "description": "A bitter rivalry exists between Eldrath and Vordekai. Both are consumed by the pursuit of mastering necromancy, but Eldrath views Vordekai as a dangerous rival and a threat to his magical supremacy. Their conflict is fueled by ambition and a desperate desire for ultimate power.",
        "reason": "Rivalry due to both wanting to master necromancy, with Eldrath seeing Vordekai as a threat."
      }
    }
  }
}
